import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { Gymnase } from './gymnase.model';

@Injectable({
  providedIn: 'root'
})
export class GymnaseService {

  private gymnasesUrl: string;

  // l'injection de dépendance. J'injecte une instance de HttpClient
  constructor(private http: HttpClient) {
    // j'initialise ma propriété gymnaseUrl avec la valeur donnée
    this.gymnasesUrl = 'http://localhost:8080/gymnases';
  }

  // Je déclare cette méthode que je peux appeler comme je veux
  public findAll(): Observable<Gymnase[]> {
    // this.http.get me permet de faire une requete Http avec la methode GET,
    // qui me retournera un Observable (=un objet asyncrone)
    // , et donc la réponse de l'Observable sera un tableau de gymnase
    // en format JSON.

    return this.http.get<Gymnase[]>(this.gymnasesUrl).pipe(
      map((gym:any[]) => gym.map(x => new Gymnase(
        x.id,
        x.nomGymnase,
        x.adresse,
        x.ville,
        x.surface,
        x.seances
      )))
    );
  }
}
